import React, { useState, useEffect } from 'react'

function AcademicRecordModal({ student, onClose }) {
  const [newRecord, setNewRecord] = useState({ subject: '', grade: '' })
  const [records, setRecords] = useState(student.academicRecords || [])
  
  // Calculate GPA (assuming grade scale out of 100)
  const calculateGPA = () => {
    if(records.length === 0) return 'N/A'
    const total = records.reduce((sum, rec) => sum + Number(rec.grade), 0)
    const avg = total / records.length
    return (avg / 25).toFixed(2)
  }

  const addRecord = () => {
    if(newRecord.subject && newRecord.grade){
      const updated = [...records, newRecord]
      setRecords(updated)
      setNewRecord({ subject: '', grade: '' })
      // Update the student record in localStorage
      const students = JSON.parse(localStorage.getItem('students')) || []
      const updatedStudents = students.map(s => s.id === student.id ? { ...s, academicRecords: updated } : s)
      localStorage.setItem('students', JSON.stringify(updatedStudents))
    }
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="w-full max-w-xl bg-[rgba(40,40,40,0.8)] backdrop-blur-md p-8 rounded shadow-lg">
        <h2 className="text-xl font-bold mb-4">Academic Records for {student.name}</h2>
        <table className="w-full text-sm mb-4">
          <thead>
            <tr>
              <th className="border px-2 py-1">Subject</th>
              <th className="border px-2 py-1">Grade</th>
            </tr>
          </thead>
          <tbody>
            {records.map((rec, idx) => (
              <tr key={idx}>
                <td className="border px-2 py-1">{rec.subject}</td>
                <td className="border px-2 py-1">{rec.grade}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <p className="mb-2">GPA: {calculateGPA()}</p>
        <div className="flex gap-2 mb-4">
          <input type="text" placeholder="Subject" value={newRecord.subject}
            onChange={e => setNewRecord({...newRecord, subject: e.target.value})}
            className="p-2 border rounded w-1/2"/>
          <input type="number" placeholder="Grade" value={newRecord.grade}
            onChange={e => setNewRecord({...newRecord, grade: e.target.value})}
            className="p-2 border rounded w-1/2"/>
          <button onClick={addRecord} className="p-2 bg-indigo-500 rounded">Add Record</button>
        </div>
        <button onClick={onClose} className="px-4 py-2 bg-red-500 rounded">Close</button>
      </div>
    </div>
  )
}

function StudentDataPage() {
  const [students, setStudents] = useState([])
  const [search, setSearch] = useState('')
  const [modalStudent, setModalStudent] = useState(null)
  
  useEffect(() => {
    const data = JSON.parse(localStorage.getItem('students')) || []
    setStudents(data)
  }, [])
  
  const handleDelete = (id) => {
    const updated = students.filter(s => s.id !== id)
    setStudents(updated)
    localStorage.setItem('students', JSON.stringify(updated))
  }
  
  const filteredStudents = students.filter(s =>
    s.name.toLowerCase().includes(search.toLowerCase()) || s.id.includes(search))
    
  return (
    <div className="min-h-screen bg-[#121212] text-white p-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Student Data</h1>
        <input type="text" placeholder="Search by name or ID" value={search} onChange={e=>setSearch(e.target.value)}
          className="p-2 border rounded w-1/3"/>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {filteredStudents.map(student => (
          <div key={student.id} className="p-6 bg-[rgba(40,40,40,0.8)] backdrop-blur-md rounded-[16px] shadow-[8px_8px_16px_rgba(0,0,0,0.5),-8px_-8px_16px_rgba(255,255,255,0.05)] transition transform hover:scale-105 hover:shadow-[0_0_16px_rgba(255,255,255,0.1)] relative">
            <img src={student.photo || "https://via.placeholder.com/150"} alt="student" className="w-24 h-24 object-cover rounded-full mx-auto"/>
            <h2 className="mt-4 text-xl font-bold text-center">{student.name}</h2>
            <p className="text-center">Grade: {student.grade}</p>
            <p className="text-center text-xs">ID: {student.id}</p>
            <div className="mt-4 flex justify-around">
              <button onClick={() => {
                // For brevity, editing functionality can be implemented similarly.
                alert("Edit functionality not implemented")
              }} className="px-3 py-1 bg-green-500 rounded">Edit</button>
              <button onClick={() => handleDelete(student.id)} className="px-3 py-1 bg-red-500 rounded">Delete</button>
            </div>
            <div className="mt-4 flex justify-center">
              <button onClick={()=>setModalStudent(student)} className="px-3 py-1 bg-indigo-500 rounded">View Academic Records</button>
            </div>
          </div>
        ))}
      </div>
      {modalStudent && <AcademicRecordModal student={modalStudent} onClose={()=>setModalStudent(null)} />}
    </div>
  )
}

export default StudentDataPage
