import React, { useState, useEffect } from 'react'

function App() {
  const [page, setPage] = useState('registration')
  const [name, setName] = useState('')
  const [grade, setGrade] = useState(6)
  const [email, setEmail] = useState('')
  const [phone, setPhone] = useState('')
  const [photoPreview, setPhotoPreview] = useState('')
  const [activities, setActivities] = useState([])
  const [achievements, setAchievements] = useState('')
  const [errors, setErrors] = useState({})
  const [successMsg, setSuccessMsg] = useState('')
  const [students, setStudents] = useState([])
  const [search, setSearch] = useState('')
  const [modalStudent, setModalStudent] = useState(null)

  const grades = Array.from({ length: 8 }, (_, i) => i + 6)
  const activitiesOptions = ['Sports','Music','Arts','Debate','Science Club']

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem('students')) || []
    setStudents(data)
  }, [page === 'data'])

  useEffect(() => {
    let errs = {}
    if(name && name.trim().length < 3) errs.name = "Name must be at least 3 characters"
    if(email && !/^\S+@\S+\.\S+$/.test(email)) errs.email = "Enter a valid email"
    if(phone && !/^\d{10,}$/.test(phone)) errs.phone = "Phone must be at least 10 digits"
    setErrors(errs)
  }, [name, email, phone])
  
  const generateId = () => `stu-${Date.now()}-${Math.floor(Math.random()*1e4)}`

  const handleRegistrationSubmit = (e) => {
    e.preventDefault()
    if(Object.keys(errors).length > 0 || !name || !email || !phone) return
    const newStudent = {
      id: generateId(),
      name,
      grade,
      email,
      phone,
      photo: photoPreview,
      activities,
      achievements,
      academicRecords: []
    }
    const stored = JSON.parse(localStorage.getItem('students')) || []
    localStorage.setItem('students', JSON.stringify([...stored, newStudent]))
    setSuccessMsg("Registration successful!")
    setName(''); setGrade(grades[0]); setEmail(''); setPhone(''); setPhotoPreview(''); setActivities([]); setAchievements('')
    setTimeout(()=> setSuccessMsg(''), 3000)
  }
  
  const handleDelete = (id) => {
    const updated = students.filter(s => s.id !== id)
    setStudents(updated)
    localStorage.setItem('students', JSON.stringify(updated))
  }

  const renderRegistrationPage = () => (
    <div className="min-h-screen flex items-center justify-center bg-[#121212] text-white p-8">
      <div className="relative w-full max-w-lg p-8 bg-[rgba(40,40,40,0.8)] backdrop-blur-md rounded-[16px] shadow-lg animate-slideUp">
        <button onClick={()=>setPage('data')} className="absolute top-4 right-4 text-sm flex items-center">
          View Student Data
        </button>
        <h1 className="text-2xl font-bold mb-6 text-center">Student Registration</h1>
        <form onSubmit={handleRegistrationSubmit} className="space-y-4">
          <input type="text" placeholder="Name" value={name} onChange={e=>setName(e.target.value)} className="w-full p-2 border rounded" />
          {errors.name && <p className="text-red-500 text-xs">{errors.name}</p>}
          <select value={grade} onChange={e=>setGrade(e.target.value)} className="w-full p-2 border rounded">
            {grades.map(g => (<option key={g} value={g}>Grade {g}</option>))}
          </select>
          <input type="email" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} className="w-full p-2 border rounded" />
          {errors.email && <p className="text-red-500 text-xs">{errors.email}</p>}
          <input type="tel" placeholder="Phone Number" value={phone} onChange={e=>setPhone(e.target.value)} className="w-full p-2 border rounded" />
          {errors.phone && <p className="text-red-500 text-xs">{errors.phone}</p>}
          <div className="border-dashed border-2 p-4 rounded">
            <p className="mb-2">Upload Photo</p>
            <input type="file" onChange={e => {
              const file = e.target.files[0]
              if(file){
                const reader = new FileReader()
                reader.onloadend = () => setPhotoPreview(reader.result)
                reader.readAsDataURL(file)
              }
            }} className="w-full" />
            {photoPreview && <img src={photoPreview} alt="Preview" className="mt-2 h-32 mx-auto object-cover rounded" />}
          </div>
          <div>
            <p>Extracurricular Activities:</p>
            <div className="flex flex-wrap gap-2">
              {activitiesOptions.map(opt => (
                <button type="button" key={opt}
                  onClick={()=> setActivities(prev => prev.includes(opt) ? prev.filter(a=>a!==opt) : [...prev, opt])}
                  className={`px-2 py-1 border rounded ${activities.includes(opt) ? 'bg-[#03dac6] text-black' : ''}`}>
                  {opt}
                </button>
              ))}
            </div>
          </div>
          <textarea placeholder="Achievements" value={achievements} onChange={e=>setAchievements(e.target.value)} className="w-full p-2 border rounded" rows="3"></textarea>
          <button type="submit" className="w-full p-3 mt-4 bg-[rgba(40,40,40,0.8)] backdrop-blur-md rounded hover:scale-105 transition transform shadow-lg">
            Submit
          </button>
          {successMsg && <p className="text-green-400 text-center mt-2">{successMsg}</p>}
        </form>
      </div>
    </div>
  )

  const renderStudentDataPage = () => {
    const filteredStudents = students.filter(s =>
      s.name.toLowerCase().includes(search.toLowerCase()) || s.id.includes(search))
    return (
      <div className="min-h-screen bg-[#121212] text-white p-8">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Student Data</h1>
          <input type="text" placeholder="Search by name or ID" value={search} onChange={e=>setSearch(e.target.value)}
            className="p-2 border rounded w-1/3"/>
          <button onClick={()=>setPage('registration')} className="p-2 bg-indigo-500 rounded">Back to Registration</button>
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredStudents.map(student => (
            <div key={student.id} className="p-6 bg-[rgba(40,40,40,0.8)] backdrop-blur-md rounded-[16px] shadow-lg transition transform hover:scale-105 relative">
              <img src={student.photo || "https://via.placeholder.com/150"} alt="student" className="w-24 h-24 object-cover rounded-full mx-auto"/>
              <h2 className="mt-4 text-xl font-bold text-center">{student.name}</h2>
              <p className="text-center">Grade: {student.grade}</p>
              <p className="text-center text-xs">ID: {student.id}</p>
              <div className="mt-4 flex justify-around">
                <button onClick={() => alert("Edit functionality not implemented")} className="px-3 py-1 bg-green-500 rounded">Edit</button>
                <button onClick={() => handleDelete(student.id)} className="px-3 py-1 bg-red-500 rounded">Delete</button>
              </div>
              <div className="mt-4 flex justify-center">
                <button onClick={()=>setModalStudent(student)} className="px-3 py-1 bg-indigo-500 rounded">View Academic Records</button>
              </div>
            </div>
          ))}
        </div>
        {modalStudent && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className="w-full max-w-xl bg-[rgba(40,40,40,0.8)] backdrop-blur-md p-8 rounded shadow-lg">
              <h2 className="text-xl font-bold mb-4">Academic Records for {modalStudent.name}</h2>
              <button onClick={()=>setModalStudent(null)} className="px-4 py-2 bg-red-500 rounded">Close</button>
            </div>
          </div>
        )}
      </div>
    )
  }

  return page === 'registration' ? renderRegistrationPage() : renderStudentDataPage()
}

export default App
