import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { addStudent, removeStudent, updateStudent } from './redux/studentSlice'
function App() {
  const students = useSelector(state => state.students.students)
  const dispatch = useDispatch()
  const [name, setName] = useState('')
  const [grade, setGrade] = useState('')
  const [contact, setContact] = useState('')
  const [photo, setPhoto] = useState('')
  const [term, setTerm] = useState('')
  const [recordsInput, setRecordsInput] = useState('')
  const [editId, setEditId] = useState(null)
  const generateId = () => `${Date.now()}-${Math.floor(Math.random() * 1e6)}`
  const handleFileChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      const reader = new FileReader()
      reader.onloadend = () => setPhoto(reader.result)
      reader.readAsDataURL(file)
    }
  }
  const parseRecords = (input) => {
    return input.split('\n').map(line => {
      const parts = line.split(':')
      return parts.length === 2 ? { subject: parts[0].trim(), grade: Number(parts[1].trim()) } : null
    }).filter(r => r && !isNaN(r.grade))
  }
  const calculateGPA = (records) => {
    if (!records.length) return 'N/A'
    const total = records.reduce((sum, rec) => sum + rec.grade, 0)
    const avg = total / records.length
    return (avg / 25).toFixed(2)
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    if (!name.trim()) return
    const academicRecords = parseRecords(recordsInput)
    if (editId) {
      dispatch(updateStudent({ id: editId, name, grade, contact, photo, term, academicRecords }))
      setEditId(null)
    } else {
      dispatch(addStudent({ id: generateId(), name, grade, contact, photo, term, academicRecords }))
    }
    setName('')
    setGrade('')
    setContact('')
    setPhoto('')
    setTerm('')
    setRecordsInput('')
  }
  const handleEdit = student => {
    setEditId(student.id)
    setName(student.name)
    setGrade(student.grade || '')
    setContact(student.contact || '')
    setPhoto(student.photo || '')
    setTerm(student.term || '')
    setRecordsInput(student.academicRecords ? student.academicRecords.map(r => `${r.subject}: ${r.grade}`).join('\n') : '')
  }
  const handleDelete = id => dispatch(removeStudent(id))
  return (
    <div className="min-h-screen bg-gray-100 p-10">
      <header className="mb-8">
        <h1 className="text-4xl font-bold text-center">Student Tracking System</h1>
      </header>
      <form onSubmit={handleSubmit} className="mb-8 max-w-md mx-auto">
        <input type="text" placeholder="Student Name" value={name} onChange={e => setName(e.target.value)} className="w-full p-2 border rounded mb-4"/>
        <input type="text" placeholder="Grade" value={grade} onChange={e => setGrade(e.target.value)} className="w-full p-2 border rounded mb-4"/>
        <input type="text" placeholder="Contact Info" value={contact} onChange={e => setContact(e.target.value)} className="w-full p-2 border rounded mb-4"/>
        <input type="file" onChange={handleFileChange} className="w-full p-2 border rounded mb-4"/>
        <input type="text" placeholder="Term" value={term} onChange={e => setTerm(e.target.value)} className="w-full p-2 border rounded mb-4"/>
        <textarea placeholder="Academic Records (each line: subject: grade)" value={recordsInput} onChange={e => setRecordsInput(e.target.value)} className="w-full p-2 border rounded mb-4" rows="4"></textarea>
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">
          {editId ? 'Update Student' : 'Add Student'}
        </button>
      </form>
      <main className="container mx-auto">
        {students.length === 0 ? (
          <p className="text-center">No students available. Please add some students.</p>
        ) : (
          <ul>
            {students.map(student => (
              <li key={student.id} className="bg-white shadow px-4 py-2 my-2 rounded flex items-center justify-between">
                <div>
                  <div>{student.name} {student.grade && `- ${student.grade}`}</div>
                  <div>{student.contact}</div>
                  {student.photo && <img src={student.photo} alt="student" className="w-20 h-20 object-cover mt-2 rounded"/>}
                  {student.term && <div>Term: {student.term}</div>}
                  {student.academicRecords && student.academicRecords.length > 0 && <div>GPA: {calculateGPA(student.academicRecords)}</div>}
                </div>
                <div className="flex space-x-2">
                  <button onClick={() => handleEdit(student)} className="bg-green-500 text-white p-2 rounded">Edit</button>
                  <button onClick={() => handleDelete(student.id)} className="bg-red-500 text-white p-2 rounded">Delete</button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </main>
    </div>
  )
}
export default App
