import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'

const grades = Array.from({length: 8}, (_, i) => i + 6)
const activitiesOptions = ['Sports','Music','Arts','Debate','Science Club']

function RegistrationPage() {
  const navigate = useNavigate()
  const [name, setName] = useState('')
  const [grade, setGrade] = useState(grades[0])
  const [email, setEmail] = useState('')
  const [phone, setPhone] = useState('')
  const [photo, setPhoto] = useState(null)
  const [photoPreview, setPhotoPreview] = useState('')
  const [activities, setActivities] = useState([])
  const [achievements, setAchievements] = useState('')
  const [errors, setErrors] = useState({})
  const [successMsg, setSuccessMsg] = useState('')

  useEffect(() => {
    let errs = {}
    if(name && name.trim().length < 3) errs.name = "Name must be at least 3 characters"
    if(email && !/^\S+@\S+\.\S+$/.test(email)) errs.email = "Enter a valid email"
    if(phone && !/^\d{10,}$/.test(phone)) errs.phone = "Phone must be at least 10 digits"
    setErrors(errs)
  }, [name, email, phone])

  const handlePhotoUpload = (e) => {
    const file = e.target.files[0]
    if(file){
      setPhoto(file)
      const reader = new FileReader()
      reader.onloadend = () => setPhotoPreview(reader.result)
      reader.readAsDataURL(file)
    }
  }
  
  const generateId = () => `stu-${Date.now()}-${Math.floor(Math.random()*1e4)}`
  
  const handleSubmit = (e) => {
    e.preventDefault()
    if(Object.keys(errors).length > 0 || !name || !email || !phone) return
    const newStudent = {
      id: generateId(),
      name, grade, email, phone,
      photo: photoPreview,
      activities, achievements,
      academicRecords: []
    }
    const students = JSON.parse(localStorage.getItem('students')) || []
    localStorage.setItem('students', JSON.stringify([...students, newStudent]))
    setSuccessMsg("Registration successful!")
    setName(''); setGrade(grades[0]); setEmail(''); setPhone(''); setPhoto(null); setPhotoPreview(''); setActivities([]); setAchievements('')
    setTimeout(()=> setSuccessMsg(''), 3000)
  }

  const handleReset = () => {
    setName(''); setGrade(grades[0]); setEmail(''); setPhone(''); setPhoto(null); setPhotoPreview(''); setActivities([]); setAchievements('')
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#121212] p-8 animate-fadeIn">
      <div className="w-[600px] p-8 rounded-[16px] bg-[rgba(40,40,40,0.8)] backdrop-blur-md shadow-[8px_8px_16px_rgba(0,0,0,0.5),-8px_-8px_16px_rgba(255,255,255,0.05)] relative">
        <button onClick={()=>navigate('/data')} className="absolute top-4 right-4 text-sm">
          View Student Data
        </button>
        <h1 className="text-2xl font-semibold text-center text-[#bb86fc] mb-8">Register a New Student</h1>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-[14px] font-medium text-[#bb86fc] mb-2">Name</label>
            <input type="text" placeholder="Enter full name" value={name} onChange={e=>setName(e.target.value)}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white placeholder-[rgba(255,255,255,0.5)] text-[16px] transition hover:border-[#bb86fc] focus:border-[#03dac6] focus:shadow-[0_0_8px_rgba(3,218,198,0.5)] w-full" />
            {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
          </div>
          <div>
            <label className="block text-[14px] font-medium text-[#bb86fc] mb-2">Email</label>
            <input type="email" placeholder="Enter email address" value={email} onChange={e=>setEmail(e.target.value)}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white placeholder-[rgba(255,255,255,0.5)] text-[16px] transition hover:border-[#bb86fc] focus:border-[#03dac6] focus:shadow-[0_0_8px_rgba(3,218,198,0.5)] w-full" />
            {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
          </div>
          <div>
            <label className="block text-[14px] font-medium text-[#bb86fc] mb-2">Phone Number</label>
            <input type="tel" placeholder="Enter phone number" value={phone} onChange={e=>setPhone(e.target.value)}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white placeholder-[rgba(255,255,255,0.5)] text-[16px] transition hover:border-[#bb86fc] focus:border-[#03dac6] focus:shadow-[0_0_8px_rgba(3,218,198,0.5)] w-full" />
            {errors.phone && <p className="text-red-500 text-xs mt-1">{errors.phone}</p>}
          </div>
          <div>
            <label className="block text-[14px] font-medium text-[#bb86fc] mb-2">Grade</label>
            <select value={grade} onChange={e=>setGrade(e.target.value)}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white text-[16px] transition hover:border-[#bb86fc] focus:border-[#03dac6] focus:shadow-[0_0_8px_rgba(3,218,198,0.5)] w-full">
              {grades.map(g => (<option key={g} value={g}>Grade {g}</option>))}
            </select>
          </div>
          <div>
            <label className="block text-[14px] font-medium text-[#bb86fc] mb-2">Achievements</label>
            <textarea placeholder="Enter achievements" value={achievements} onChange={e=>setAchievements(e.target.value)}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white placeholder-[rgba(255,255,255,0.5)] text-[16px] transition hover:border-[#bb86fc] focus:border-[#03dac6] focus:shadow-[0_0_8px_rgba(3,218,198,0.5)] w-full resize-none" rows="2"></textarea>
          </div>
          <div>
            <label className="block text-[14px] font-medium text-[#bb86fc] mb-2">Photo</label>
            <div className="border-2 border-dashed border-[rgba(255,255,255,0.2)] rounded p-4 flex flex-col items-center justify-center cursor-pointer transition hover:border-[#bb86fc]">
              {photoPreview 
                ? <img src={photoPreview} alt="Preview" className="h-20 w-20 rounded-full object-cover" />
                : <div className="flex flex-col items-center">
                    <span className="text-white mb-1">Drag & Drop Photo Here</span>
                    <span className="text-[12px] text-[rgba(255,255,255,0.5)]">or click to upload</span>
                  </div>
              }
              <input type="file" accept="image/jpeg,image/png" onChange={handlePhotoUpload} className="hidden" />
            </div>
          </div>
          <div className="flex flex-col gap-4 mt-4">
            <button type="submit" className="w-full h-12 rounded bg-[#bb86fc] text-white text-[16px] font-medium transition hover:bg-[#03dac6] hover:shadow-[0_0_8px_rgba(3,218,198,0.5)]">
              Submit
            </button>
            <button type="button" onClick={handleReset} className="w-full text-white text-[14px] flex items-center justify-center gap-2">
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v6h6M20 20v-6h-6" />
              </svg>
              Reset
            </button>
          </div>
          {successMsg && <div className="mt-4 text-green-400 text-center">{successMsg}</div>}
        </form>
      </div>
    </div>
  )
}

export default RegistrationPage
