import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'

const grades = Array.from({length: 8}, (_, i) => i + 6)
const activitiesOptions = ['Sports','Music','Arts','Debate','Science Club']

function RegistrationPage() {
  const navigate = useNavigate()
  const [name, setName] = useState('')
  const [grade, setGrade] = useState(grades[0])
  const [email, setEmail] = useState('')
  const [phone, setPhone] = useState('')
  const [photo, setPhoto] = useState(null)
  const [photoPreview, setPhotoPreview] = useState('')
  const [activities, setActivities] = useState([])
  const [achievements, setAchievements] = useState('')
  const [errors, setErrors] = useState({})
  const [successMsg, setSuccessMsg] = useState('')

  // Real-time validation
  useEffect(() => {
    let errs = {}
    if(name && name.trim().length < 3) errs.name = "Name must be at least 3 characters"
    if(email && !/^\S+@\S+\.\S+$/.test(email)) errs.email = "Enter a valid email"
    if(phone && !/^\d{10,}$/.test(phone)) errs.phone = "Phone must be at least 10 digits"
    setErrors(errs)
  }, [name, email, phone])

  const handlePhotoUpload = (e) => {
    const file = e.target.files[0]
    if(file){
      setPhoto(file)
      const reader = new FileReader()
      reader.onloadend = () => setPhotoPreview(reader.result)
      reader.readAsDataURL(file)
    }
  }
  
  const generateId = () => `stu-${Date.now()}-${Math.floor(Math.random()*1e4)}`
  
  const handleSubmit = (e) => {
    e.preventDefault()
    if(Object.keys(errors).length > 0 || !name || !email || !phone) return
    const newStudent = {
      id: generateId(),
      name, grade, email, phone,
      photo: photoPreview,
      activities, achievements,
      academicRecords: []
    }
    // Save in localStorage
    const students = JSON.parse(localStorage.getItem('students')) || []
    localStorage.setItem('students', JSON.stringify([...students, newStudent]))
    setSuccessMsg("Registration successful!")
    // Clear form after submission
    setName(''); setGrade(grades[0]); setEmail(''); setPhone(''); setPhoto(null); setPhotoPreview(''); setActivities([]); setAchievements('')
    setTimeout(()=> setSuccessMsg(''), 3000)
  }
  
  // Toggle multi-select options
  const handleActivityChange = (activity) => {
    setActivities(prev => prev.includes(activity) ? prev.filter(a => a!==activity) : [...prev, activity])
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#121212] text-white p-8">
      <div className="relative w-full max-w-lg p-8 bg-[rgba(40,40,40,0.8)] backdrop-blur-md rounded-[16px] shadow-[8px_8px_16px_rgba(0,0,0,0.5),-8px_-8px_16px_rgba(255,255,255,0.05)] animate-slideUp">
        <button onClick={()=>navigate('/data')} className="absolute top-4 right-4 text-sm flex items-center">
          {/* Icon could be added here */}
          View Student Data
        </button>
        <h1 className="text-2xl font-bold mb-6 text-center">Student Registration</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input type="text" placeholder="Name" value={name} onChange={e=>setName(e.target.value)} className="w-full p-2 border rounded" />
          {errors.name && <p className="text-red-500 text-xs">{errors.name}</p>}
          <select value={grade} onChange={e=>setGrade(e.target.value)} className="w-full p-2 border rounded">
            {grades.map(g=> (<option key={g} value={g}>Grade {g}</option>))}
          </select>
          <input type="email" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} className="w-full p-2 border rounded" />
          {errors.email && <p className="text-red-500 text-xs">{errors.email}</p>}
          <input type="tel" placeholder="Phone Number" value={phone} onChange={e=>setPhone(e.target.value)} className="w-full p-2 border rounded" />
          {errors.phone && <p className="text-red-500 text-xs">{errors.phone}</p>}
          <div className="border-dashed border-2 p-4 rounded">
            <p className="mb-2">Drag & Drop Photo or click to upload</p>
            <input type="file" onChange={handlePhotoUpload} className="w-full" />
            {photoPreview && <img src={photoPreview} alt="Preview" className="mt-2 h-32 mx-auto object-cover rounded" />}
          </div>
          <div>
            <p>Extracurricular Activities:</p>
            <div className="flex flex-wrap gap-2">
              {activitiesOptions.map(opt=> (
                <button type="button" key={opt}
                  onClick={()=>handleActivityChange(opt)}
                  className={`px-2 py-1 border rounded ${activities.includes(opt) ? 'bg-[#03dac6] text-black' : ''}`}>
                  {opt}
                </button>
              ))}
            </div>
          </div>
          <textarea placeholder="Achievements" value={achievements} onChange={e=>setAchievements(e.target.value)} className="w-full p-2 border rounded" rows="3"></textarea>
          <button type="submit" className="w-full p-3 mt-4 bg-[rgba(40,40,40,0.8)] backdrop-blur-md rounded hover:scale-105 transition transform shadow-[0_0_16px_rgba(255,255,255,0.1)]">
            Submit
          </button>
          {successMsg && <p className="text-green-400 text-center mt-2">{successMsg}</p>}
        </form>
      </div>
    </div>
  )
}

export default RegistrationPage
