import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addStudent } from './redux/studentSlice';

function App() {
	const students = useSelector(state => state.students.students);
	const dispatch = useDispatch();
	const [name, setName] = useState('');
	const [activity, setActivity] = useState('');

	const handleAddStudent = (e) => {
		e.preventDefault();
		if (!name.trim()) return;
		dispatch(addStudent({
		    id: Date.now().toString(),
		    name,
		    activity
		}));
		setName('');
		setActivity('');
	};

	return (
		<div className="min-h-screen bg-gray-100 p-10">
			<header className="mb-8">
				<h1 className="text-4xl font-bold text-center">Student Tracking System</h1>
			</header>
			
			{/* Add Student Form */}
			<form onSubmit={handleAddStudent} className="mb-8 max-w-md mx-auto">
				<input 
					type="text" 
					placeholder="Student Name" 
					value={name} 
					onChange={(e) => setName(e.target.value)} 
					className="w-full p-2 border rounded mb-4"
				/>
				<input 
					type="text" 
					placeholder="Activity" 
					value={activity} 
					onChange={(e) => setActivity(e.target.value)} 
					className="w-full p-2 border rounded mb-4"
				/>
				<button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">
					Add Student
				</button>
			</form>
			
			<main className="container mx-auto">
				{students.length === 0 ? (
					<p className="text-center">No students available. Please add some students.</p>
				) : (
					<ul>
						{students.map(student => (
							<li key={student.id} className="bg-white shadow px-4 py-2 my-2 rounded">
								{student.name} - {student.activity || 'No activities'}
							</li>
						))}
					</ul>
				)}
			</main>
		</div>
	);
}

export default App;
