import React, { useState, useRef } from 'react'

function StudentRegistrationForm() {
  const [name, setName] = useState('')
  const [grade, setGrade] = useState(6)
  const [email, setEmail] = useState('')
  const [phone, setPhone] = useState('')
  const [achievements, setAchievements] = useState('')
  const [photoPreview, setPhotoPreview] = useState('')
  const [errors, setErrors] = useState({})
  const [successMsg, setSuccessMsg] = useState('')
  const fileInputRef = useRef(null)
  const grades = Array.from({ length: 8 }, (_, i) => i + 6)

  const validateFields = () => {
    const newErrors = {}
    if(name.trim().length < 3) newErrors.name = 'Name must be at least 3 characters'
    if(!/^\S+@\S+\.\S+$/.test(email)) newErrors.email = 'Enter a valid email'
    if(!/^\d{10,}$/.test(phone)) newErrors.phone = 'Phone must be at least 10 digits'
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleFile = file => {
    if(file && (file.type === 'image/jpeg' || file.type === 'image/png')){
      const reader = new FileReader()
      reader.onloadend = () => setPhotoPreview(reader.result)
      reader.readAsDataURL(file)
    }
  }

  const handleDrop = e => {
    e.preventDefault()
    if(e.dataTransfer.files && e.dataTransfer.files[0]){
      handleFile(e.dataTransfer.files[0])
    }
  }

  const handleDragOver = e => {
    e.preventDefault()
  }

  const handleSubmit = e => {
    e.preventDefault()
    if(!validateFields()) return
    setSuccessMsg('Registration successful!')
    setName(''); setGrade(grades[0]); setEmail(''); setPhone(''); setAchievements(''); setPhotoPreview('')
    setTimeout(() => setSuccessMsg(''), 3000)
  }

  const handleReset = () => {
    setName(''); setGrade(grades[0]); setEmail(''); setPhone(''); setAchievements(''); setPhotoPreview('')
    setErrors({})
    if(fileInputRef.current) fileInputRef.current.value = ""
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#000000] p-8 animate-fadeIn">
      <div className="w-[600px] p-8 rounded-[16px] bg-[rgba(0,0,0,0.8)] backdrop-blur-md shadow-[8px_8px_16px_rgba(0,0,0,0.5),-8px_-8px_16px_rgba(255,215,0,0.1)] animate-float">
        <h1 className="text-[24px] font-semibold text-center text-[#ffd700] mb-8 animate-fadeInBounce">
          Register a New Student
        </h1>
        {successMsg && <div className="mb-4 p-2 text-center text-[#ffd700] animate-slideDown">{successMsg}</div>}
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Name Field */}
          <div>
            <label className="block text-[14px] font-medium text-[#1e90ff] mb-2">Name</label>
            <input
              type="text"
              placeholder="Enter full name"
              value={name}
              onChange={e => setName(e.target.value)}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white placeholder-[rgba(255,255,255,0.5)] text-[16px] transition hover:border-[#1e90ff] focus:border-[#ffd700] focus:shadow-[0_0_8px_rgba(255,215,0,0.5)] w-full"
            />
            {errors.name && <p className="text-red-500 text-xs mt-1">{errors.name}</p>}
          </div>
          {/* Email Field */}
          <div>
            <label className="block text-[14px] font-medium text-[#1e90ff] mb-2">Email</label>
            <input
              type="email"
              placeholder="Enter email address"
              value={email}
              onChange={e => setEmail(e.target.value)}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white placeholder-[rgba(255,255,255,0.5)] text-[16px] transition hover:border-[#1e90ff] focus:border-[#ffd700] focus:shadow-[0_0_8px_rgba(255,215,0,0.5)] w-full"
            />
            {errors.email && <p className="text-red-500 text-xs mt-1">{errors.email}</p>}
          </div>
          {/* Phone Field */}
          <div>
            <label className="block text-[14px] font-medium text-[#1e90ff] mb-2">Phone Number</label>
            <input
              type="tel"
              placeholder="Enter phone number"
              value={phone}
              onChange={e => setPhone(e.target.value)}
              value={phone}
              onChange={e => setPhone(e.target.value)}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white placeholder-[rgba(255,255,255,0.5)] text-[16px] transition hover:border-[#bb86fc] focus:border-[#03dac6] focus:shadow-[0_0_8px_rgba(3,218,198,0.5)] w-full"
            />
            {errors.phone && <p className="text-red-500 text-xs mt-1">{errors.phone}</p>}
          </div>
          {/* Grade Field */}
          <div>
            <label className="block text-[14px] font-medium text-[#bb86fc] mb-2">Grade</label>
            <select
              value={grade}
              onChange={e => setGrade(Number(e.target.value))}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white text-[16px] transition hover:border-[#bb86fc] focus:border-[#03dac6] focus:shadow-[0_0_8px_rgba(3,218,198,0.5)] w-full">
              {grades.map(g => (
                <option key={g} value={g}>Grade {g}</option>
              ))}
            </select>
          </div>
          {/* Achievements Field */}
          <div>
            <label className="block text-[14px] font-medium text-[#bb86fc] mb-2">Achievements</label>
            <textarea
              placeholder="Enter achievements"
              value={achievements}
              onChange={e => setAchievements(e.target.value)}
              className="h-12 p-3 rounded border border-[rgba(255,255,255,0.2)] bg-[rgba(255,255,255,0.1)] text-white placeholder-[rgba(255,255,255,0.5)] text-[16px] transition hover:border-[#bb86fc] focus:border-[#03dac6] focus:shadow-[0_0_8px_rgba(3,218,198,0.5)] w-full resize-none"
              rows="2"
            ></textarea>
          </div>
          {/* Creative Image Section: Drag and Drop */}
          <div>
            <label className="block text-[14px] font-medium text-[#bb86fc] mb-2">Photo</label>
            <div
              onDrop={handleDrop}
              onDragOver={handleDragOver}
              onClick={() => fileInputRef.current && fileInputRef.current.click()}
              className="border-2 border-dashed border-[rgba(255,255,255,0.2)] rounded p-4 flex flex-col items-center justify-center cursor-pointer transition hover:border-[#bb86fc]">
              {photoPreview ? (
                <img src={photoPreview} alt="Preview" className="h-20 w-20 rounded-full object-cover" />
              ) : (
                <div className="flex flex-col items-center">
                  <span className="text-white mb-1">Drag & Drop Photo Here</span>
                  <span className="text-[12px] text-[rgba(255,255,255,0.5)]">or click to upload</span>
                </div>
              )}
              <input ref={fileInputRef} type="file" accept="image/jpeg,image/png" onChange={e => handleFile(e.target.files[0])} className="hidden" />
            </div>
          </div>
          {/* Interactive Buttons */}
          <div className="flex flex-col gap-4">
            <button type="submit" className="w-full h-12 rounded bg-[#bb86fc] text-white text-[16px] font-medium transition hover:bg-[#03dac6] hover:shadow-[0_0_8px_rgba(3,218,198,0.5)]">
              Submit
            </button>
            <button type="button" onClick={handleReset} className="w-full text-white text-[14px] flex items-center justify-center gap-2">
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v6h6M20 20v-6h-6" />
              </svg>
              Reset
            </button>
          </div>
        </form>
      </div>
    </div>
  )
}

export default StudentRegistrationForm
